่เตือน "[/size][/b][/color]', 'เรื่องสิว....สิว', '', 'inherit', 'open', 'open', '', '139-revision-2', '', '', '2012-09-24 13:52:25', '2012-09-24 06:52:25', '', 139, 'http://fromchiangmaiwithlove.com/139-revision-2/', 0, 'revision', '', 0),
